const initialState = {
  isAuthenticated: false,
  activeUser: "Master",
  activeProject: "Test"
}

const reducer = (state = initialState, action) => {
  if (action.type === 'CER_LOGIN'){
    return {
      ...state,
      isAuthenticated: true
    }
  } else if (action.type === 'CER_LOGOUT'){
    return {
      ...state,
      isAuthenticated: false
    }
  }
  return state;
}

export default reducer;
