import React from 'react';
import LoginCerberus from './auth/login';
import HomeCerberus from './pages/index';
import ProtectedRoute from '../shared/ProtectedRoute';
import { Route, useRouteMatch, Switch, Redirect} from 'react-router-dom';
import {connect} from 'react-redux';

const Cerberus = (props) =>{  
  let match = useRouteMatch();

  const authenticate = event => {
    props.logIn();
  }

  const logout = event => {
    props.logOut();
  }

  return (
    <div className="Cerberus">
      <Switch>
        <Route path={`${match.path}/login`}>
          {
            props.isAuthenticated ? <Redirect to={`${match.url}/`}/> : <LoginCerberus success={authenticate}></LoginCerberus>
          }
        </Route>
        <ProtectedRoute path={`${match.path}/`} exact>
          <HomeCerberus logout={logout}></HomeCerberus>
        </ProtectedRoute>
      </Switch>
    </div>
  );
}

const mapStateToProps = state => {
  return {
    isAuthenticated: state.isAuthenticated
  };
} 

const mapDispatchToProps = dispatch => {
  return {
    logIn: () => dispatch({type: 'CER_LOGIN'}),
    logOut: () => dispatch({type: 'CER_LOGOUT'})
  }
}
export default connect(mapStateToProps,mapDispatchToProps)(Cerberus);
