import React from 'react';
import Sidebar from '../../shared/sidebar';
import Topnav from '../../shared/topnav';
import { Route, Switch} from 'react-router-dom';
import {connect} from 'react-redux';

const HomeCerberus = (props) => {
  let data = {
    sidebar_logo: {
      svg: '/logo/Cerberus_logo.svg',
      name: 'cerberus'
    },
    sidebar_items: [
      {
        id:'sb_m_01',
        icon: 'fas fa-file-alt',
        main: 'Documentation',
        subs: [
          {
            id:'sb_s_01_01',
            name: 'Requirement',
            link: '/cerberus/documentation/requirements'
          },
          {
            id:'sb_s_01_02',
            name: 'Test Plan',
            link: '/cerberus/documentation/testplans'
          },
          {
            id:'sb_s_01_03',
            name: 'Report',
            link: '/cerberus/documentation/report'
          }
        ]
      },
      {
        id:'sb_m_02',
        icon: 'fas fa-vial',
        main: 'Tools',
        subs: [
          {
            id:'sb_s_02_01',
            name: 'Overview',
            link: '/cerberus/testmanager/overview'
          },
          {
            id:'sb_s_02_02',
            name: 'Test set',
            link: '/cerberus/testmanager/testset'
          },
          {
            id:'sb_s_02_03',
            name: 'Template',
            link: '/cerberus/testmanager/tmp'
          },
          {
            id:'sb_s_02_04',
            name: 'Execution',
            link: '/cerberus/testmanager/execution'
          }
        ]
      },
      {
        id:'sb_m_03',
        icon: 'far fa-smile',
        main: 'User',
        subs: [
          {
            id:'sb_s_03_01',
            name: 'Role',
            link: '/cerberus/user/roles'
          }
        ]
      }
    ]
  };
  let appRoot = "/cerberus/"
  return (
    <div className="homeCerberus">
      <Topnav username={props.activeUser} logout={props.logout}></Topnav>
      <Sidebar logo={data.sidebar_logo} items={data.sidebar_items}/>
      <Switch>
        <Route path={appRoot} component={Dashboard} props={props.cerberusUser}></Route>
      </Switch>
      </div>
    );
}

const Dashboard = props => {
  return (
    <div className="appContent">
      <div className="hero is-primary is-bold">
        <div className="hero-body">
          <h1 className="title">Hello, {props.activeUser}</h1>
        </div>
      </div>
      <div className="project">
        <div className="tile is-ancestor">
          <div className="tile is-parent is-8">
            <article className="tile is-child box">
              <h1 className="title">PROJECT</h1>
              <h2 className="subtitle">{props.activeProject}</h2>
            </article> 
          </div>
          <div className="tile is-verticle is-parent">
            <div className="tile is-child box">
              <p className="title"> 100k </p>
              <p className="subtitle"> Test plans </p>
            </div>
            <div className="tile is-child box">
              <p className="title"> 100k </p>
              <p className="subtitle"> Test sets </p>
            </div>
          </div>
        </div>
      </div>
      <div className="info-tiles">
        <div className="tile is-ancestor has-text-centered">
          <div className="tile is-parent">
            <article className="tile is-child box">
              <p className="title"> 100k </p>
              <p className="subtitle"> Test plans </p>
            </article>
          </div>
          <div className="tile is-parent">
            <article className="tile is-child box">
              <p className="title"> 100k </p>
              <p className="subtitle"> Test sets </p>
            </article>
          </div>
          <div className="tile is-parent">
            <article className="tile is-child box">
              <p className="title"> 100k </p>
              <p className="subtitle"> Tests </p>
            </article>
          </div>
          <div className="tile is-parent">
            <article className="tile is-child box">
              <p className="title"> 100k </p>
              <p className="subtitle"> Features </p>
            </article>
          </div>
        </div>
      </div>
    </div>
  );
}
const mapStateToProps = state => {
  return {
    isAuthenticated: state.isAuthenticated,
    activeUser: state.activeUser,
    activeProject: state.activeProject
  };
}

const mapDispatchToProps = dispatch => {
  return {
    changeProject: (name) => dispatch({type: 'CER_PROJ_CHANGE',value: name}),
  }
}
export default connect(mapStateToProps,mapDispatchToProps)(HomeCerberus);
