import React, { Component } from 'react';
// import logo from './logo.svg';
import './font-awesome/css/all.min.css';
import Topnav from './components/shared/topnav';
import Sidebar from './components/shared/sidebar';
import TestsetList from './components/testcase/testsetList';

class App extends Component {
  state = {
    showMenu: false,
    sidebar_items: [
      {
        icons: '',
        main: 'Documentations',
        subs: [
          {
            name: 'Requirements',
            link: '/cerberus/documentation/requirements'
          },
          {
            name: 'Test Plans',
            link: '/cerberus/documentation/testplans'
          },
          {
            name: 'Tracebility Matrix',
            link: '/cerberus/documentation/tracematrix'
          }
        ]
      },
      {
        icons: '',
        main: 'Test Management',
        subs: [
          {
            name: 'Project overviews',
            link: '/cerberus/testmanager/overview'
          },
          {
            name: 'Test sets',
            link: '/cerberus/testmanager/testsets'
          },
          {
            name: 'Test templates',
            link: '/cerberus/testmanager/testtmps'
          },
          {
            name: 'Executions',
            link: '/cerberus/testmanager/executions'
          }
        ]
      },
      {
        icons: '',
        main: 'Users Management',
        subs: [
          {
            name: 'Role managements',
            link: '/cerberus/user/roles'
          }
        ]
      }
    ]
  };

  toggleMenu = () => {
    let currentStatus = this.state.showMenu
    this.setState({showMenu: !currentStatus});
  }

  render(){

    let menu = null;

    if (this.state.showMenu){
      menu = <TestsetList/>
    }
    return (
    <div className="App">
      <Topnav username="admin"/>
      <Sidebar logo="" items={this.state.sidebar_items}/>
      {menu}
    </div> 
    );}
}

export default App;
