import React from 'react';
import LoginCerberus from './login';
import HomeCerberus from './home';
import { Route, useRouteMatch, Switch, Redirect } from 'react-router-dom';


const Cerberus = () =>{  
  let match = useRouteMatch();
  let loggedIn = false;
  const authChange = event => {
    console.log(event.target);
  } 
  return (
    <div className="Cerberus">
      <Switch>
        <Route path={`${match.path}/login`}>
          {
            loggedIn ? <Redirect to={`${match.url}/`} />: <LoginCerberus changed={authChange()}></LoginCerberus>
          }
        </Route>
        <Route path={`${match.path}/`} exact >
          {
            loggedIn ? <HomeCerberus auth={loggedIn}></HomeCerberus> :  <Redirect to={`${match.url}/login`}/>
          }
        </Route>
      </Switch>
    </div> 
  );
}

export default Cerberus;
