import React from 'react';
import LoginCerberus from './auth/login';
import HomeCerberus from './pages/home';
import { Route, useRouteMatch, Switch, Redirect} from 'react-router-dom';
import { useState } from 'react';
import {connect} from 'react-redux';

const Cerberus = () =>{  
  let match = useRouteMatch();

  const authenticate = event => {
    // setAuth({isAuthenticated: true});
    console.log(event);
  }

  const logout = event => {
    // setAuth({isAuthenticated: false});
    console.log(event);
  }

  const ProtectedRoute = ({children, ...rest}) => {
    return (
      <Route
        {...rest}
        render={() =>
          props.isAuthenticated ? (children) : 
          (<Redirect to={'/cerberus/login'}/>)
        }
      />
    );
  }

  return (
    <div className="Cerberus">
      <Switch>
        <Route path={`${match.path}/login`}>
          {
            props.isAuthenticated ? <Redirect to={`${match.url}/`}/> : <LoginCerberus success={authenticate}></LoginCerberus>
          }
        </Route>
        <ProtectedRoute path={`${match.path}/`} exact>
          <HomeCerberus logout={logout}></HomeCerberus>
        </ProtectedRoute>
      </Switch>
    </div>
  );
}

const mapStateToProps = state => {
  return {
    isAuthenticated: state.isAuthenticated
  };
} 

export default connect(mapStateToProps)(Cerberus);
