import React from 'react';
import LoginCerberus from './login';
import HomeCerberus from './home';
import { Route, useRouteMatch, Switch, Redirect } from 'react-router-dom';
import { useState } from 'react';


const Cerberus = () =>{  
  let match = useRouteMatch();
  const [loggedIn,setLoggedIn] = useState(false);
  const [authUser,setAuthUser] = useState("");
  const authSuccess = event => {
    setLoggedIn(true);
    setAuthUser(event.target.user);
  } 
  return (
    <div className="Cerberus">
      <Switch>
        <Route path={`${match.path}/login`}>
          {
            loggedIn ? <Redirect to={`${match.url}/`} />: <LoginCerberus success={authSuccess}></LoginCerberus>
          }
        </Route>
        <Route path={`${match.path}/`} exact >
          {
            loggedIn ? <HomeCerberus user={authUser}></HomeCerberus> :  <Redirect to={`${match.url}/login`}/>
          }
        </Route>
      </Switch>
    </div> 
  );
}

export default Cerberus;
