import React from 'react';
import LoginCerberus from './login';
import HomeCerberus from './home';
import { Route, useRouteMatch, Switch, Redirect} from 'react-router-dom';
import { useState } from 'react';


const Cerberus = () =>{  
  let match = useRouteMatch();
  const auth = {
    isAuthenticated: false,
    user: "",
    authenticate(cb){
      auth.isAuthenticated = true;
      setTimeout(cb,100);
    },
    signout(cb){
      auth.isAuthenticated = false;
      setTimeout(cb,100);
    }
  };

  const ProtectedRoute = ({children, ...rest}) => {
    return (
      <Route
        {...rest}
        render={({ location }) =>
          auth.isAuthenticated ? (
            children
          ) : (
            <Redirect
              to={{
                pathname: `${match.url}/login`,
                state: { from: location }
              }}
            />
          )
        }
      />
    );
  }
  const authSuccess = () => {
    // let history = useHistory();
    // let location = useLocation();
    // let {from} = location.state || {from: {pathname: `${match.url}/`}}
    // auth.authenticate(()=>{
    //   history.replace(from);
    // });
    // return (
    //   <div>Success</div>
    // );
    setlogIn(true);
  } 
  return (
    <div className="Cerberus">
      <Switch>
        <Route path={`${match.path}/login`}>
          {
            auth.isAuthenticated ? <Redirect to={`${match.url}/`}/> : <LoginCerberus success={authSuccess}></LoginCerberus>
          }
        </Route>
        <ProtectedRoute path={`${match.path}/`} exact component={HomeCerberus}/>
        {/* <Route path={`${match.path}/`} exact>
          auth.isAuthenticated ?  <HomeCerberus></HomeCerberus>: <Redirect to={`${match.url}/`}/>
        </Route> */}
      </Switch>
    </div> 
  );
}

export default Cerberus;
