const initialState = {
  isAuthenticated: false,
  activeUser: "Master",
  activeProject: "Hello_World",
  Testsets: [
    {
      id: "ts_01",
      name: "testset_d3m0_1"
    }
  ]
}

const reducer = (state = initialState, action) => {
  switch (action.type) {
    case 'CER_LOGIN':
      return {
        ...state,
        isAuthenticated: true,
        activeUser: action.value
      };
    case 'CER_LOGOUT':
      return {
        ...state,
        isAuthenticated: false
      };
    default:
      return state;
  }
}

export default reducer;
