import React from 'react';
import LoginCerberus from './auth/login';
import { Route, Switch, Redirect} from 'react-router-dom';
import {connect} from 'react-redux';

import Sidebar from '../shared/sidebar';
import Topnav from '../shared/topnav';
import Dashboard from './pages/dashboard';
import Testsets from './pages/dashboard';

const Cerberus = (props) =>{  
  const rootPath= "/cerberus";

  const ProtectedRoute = ({children, ...rest}) => {
    return (
      <Route
        {...rest}
        render={() =>
          props.isAuthenticated ? (children) : 
          (<Redirect to={'/cerberus/login'}/>)
        }
      />
    );
  }

  return (
    <div className="Cerberus">
      <Switch>
        <Route path={`${rootPath}/login`}>
          {
            props.isAuthenticated ? <Redirect to={`${rootPath}/`}/> : <LoginCerberus></LoginCerberus>
          }
        </Route>
        <ProtectedRoute path={`${rootPath}/`} exact>
          <div className="homeCerberus">
            <Topnav username={props.activeUser}></Topnav>
            <Sidebar logo={data.sidebar_logo} items={data.sidebar_items}/>
            <Switch>
              <ProtectedRoute path={`${rootPath}`}>
                {
                  <Dashboard  user={props.activeUser} project={props.activeProject} changeProj={props.changeProject}></Dashboard>
                }
              </ProtectedRoute>
              <ProtectedRoute path={`${rootPath}/testsets`}>
                {
                  <Testsets></Testsets>
                }
              </ProtectedRoute>
            </Switch>
          </div>
        </ProtectedRoute>
      </Switch>
    </div>
  );
}

const mapStateToProps = state => {
  return {
    isAuthenticated: state.isAuthenticated,
    activeUser: state.activeUser,
    activeProject: state.activeProject,
    Testsets: state.Testsets
  };
}

const mapDispatchToProps = dispatch => {
  return {
    changeProject: (name) => dispatch({type: 'CER_PROJ_CHANGE',value: name}),
    logOut: () => dispatch({type: 'CER_LOGOUT'})
  }
}

export default connect(mapStateToProps,mapDispatchToProps)(Cerberus);
