import React, {useState} from 'react';

const LoginCerberus = (props) => {
  const maxfieldLength = 50; 
  const [user,setUser]= useState();
  const [pass,setPass]= useState();
  let intro = {
    svg: '/logo/Cerberus_logo_black.svg',
    name: 'cerberus'
  };

  const validateForm = () => {
    let validUser = user.length > 0;
    let validPass = pass.length > 0;
    return validUser && validPass;
  }

  const handleLogin = event => {
    event.preventDefault();
  } 

  return (
    <div className="login">
      <div className="intro">
          <h2 className="subtitle">THE NEW ERA OF</h2>
          <h1 className="title">TEST MANAGEMENT</h1>
      </div>
      <div id="login" className="loginForm">
        <div className="brand">
          <img src={process.env.PUBLIC_URL + intro.svg} alt="" width="100px"/>
          <span>CERBERUS</span>
        </div>
        <h1 className="form-title">SIGN IN</h1>
        <div className="field">
          <div className="control">
            <input type="text" className="input" placeholder="Username" maxlength={maxfieldLength} value={user}/>
          </div>
        </div>
        <div className="field">
          <div className="control">
            <input type="password" className="input" placeholder="Password"maxLength={maxfieldLength} value={pass}/>
          </div>
        </div>
        <div className="field">
          <div className="control">
            <button class="button is-medium is-fullwidth" type='submit' disabled={!validateForm()}></button>
          </div>
        </div>
      </div>
    </div> 
    );
}

export default LoginCerberus;
