import React from 'react';
import LoginCerberus from './login';
import HomeCerberus from './home';
import { Route, useRouteMatch, Switch, Redirect} from 'react-router-dom';


const Cerberus = () =>{  
  let match = useRouteMatch();

  const auth = {
    isAuthenticated: false,
    authenticate(cb) {
      auth.isAuthenticated = true;
      // setTimeout(cb, 100);
    },
    signout(cb) {
      auth.isAuthenticated = false;
      // setTimeout(cb, 100);
    }
  };

  const successAuth = event => {
    auth.authenticate();
    console.log(auth.isAuthenticated);
  }

  const ProtectedRoute = ({children, ...rest}) => {
    console.log(auth.isAuthenticated);
    return (
      <Route
        {...rest}
        render={() =>
          auth.isAuthenticated ? (children) : 
          (<Redirect to={`${match.url}login`}/>)
        }
      />
    );
  }
  return (
    <div className="Cerberus">
      <Switch>
        <Route path={`${match.path}/login`}>
          {
            auth.isAuthenticated ? <Redirect to={`${match.url}/`}/> : <LoginCerberus success={successAuth}></LoginCerberus>
          }
        </Route>
        <ProtectedRoute path={`${match.path}/`} exact>
          <HomeCerberus></HomeCerberus>
        </ProtectedRoute>
      </Switch>
    </div>
  );
}

export default Cerberus;
