import React from 'react';
import LoginCerberus from './login';
import HomeCerberus from './home';
import { Route, useRouteMatch, Switch, Redirect} from 'react-router-dom';
import { useState } from 'react';


const Cerberus = () =>{  
  let match = useRouteMatch();

  const [auth,setAuth] = useState({isAuthenticated: false});

  const authenticate = event => {
    setAuth({isAuthenticated: true});
  }

  const logout = event => {
    setAuth({isAuthenticated: false});
  }

  const ProtectedRoute = ({children, ...rest}) => {
    console.log(auth.isAuthenticated);
    return (
      <Route
        {...rest}
        render={() =>
          auth.isAuthenticated ? (children) : 
          (<Redirect to={`${match.url}login`}/>)
        }
      />
    );
  }

  return (
    <div className="Cerberus">
      <Switch>
        <Route path={`${match.path}/login`}>
          {
            auth.isAuthenticated ? <Redirect to={`${match.url}/`}/> : <LoginCerberus success={authenticate}></LoginCerberus>
          }
        </Route>
        <ProtectedRoute path={`${match.path}/`} exact>
          <HomeCerberus logout={logout}></HomeCerberus>
        </ProtectedRoute>
      </Switch>
    </div>
  );
}

export default Cerberus;
