import React, { Component } from 'react';
import Sidebar from '../shared/sidebar';
// import { Route } from 'react-router-dom';

class App extends Component {
  state = {
    showMenu: false,
    sidebar_items: [
      {
        id:'sb_m_01',
        icon: 'fas fa-file-alt',
        main: 'Documentation',
        subs: [
          {
            id:'sb_s_01_01',
            name: 'Requirement',
            link: '/cerberus/documentation/requirements'
          },
          {
            id:'sb_s_01_02',
            name: 'Test Plan',
            link: '/cerberus/documentation/testplans'
          },
          {
            id:'sb_s_01_03',
            name: 'Report',
            link: '/cerberus/documentation/report'
          }
        ]
      },
      {
        id:'sb_m_02',
        icon: 'fas fa-vial',
        main: 'Tools',
        subs: [
          {
            id:'sb_s_02_01',
            name: 'Overview',
            link: '/cerberus/testmanager/overview'
          },
          {
            id:'sb_s_02_02',
            name: 'Test set',
            link: '/cerberus/testmanager/testset'
          },
          {
            id:'sb_s_02_03',
            name: 'Template',
            link: '/cerberus/testmanager/tmp'
          },
          {
            id:'sb_s_02_04',
            name: 'Execution',
            link: '/cerberus/testmanager/execution'
          }
        ]
      },
      {
        id:'sb_m_03',
        icon: 'far fa-smile',
        main: 'User',
        subs: [
          {
            id:'sb_s_03_01',
            name: 'Role',
            link: '/cerberus/user/roles'
          }
        ]
      }
    ]
  };

  toggleMenu = () => {
    let currentStatus = this.state.showMenu
    this.setState({showMenu: !currentStatus});
  }
  
  render(){
    
    return (
    <div className="Cerberus">
      <Sidebar logo="" items={this.state.sidebar_items}/>
    </div> 
    );}
}

export default App;
