const initialState = {
  isAuthenticated: false,
  activeUser: "tuanld",
  activeProject: "Hello_World",
  Testsets: [
    {
      id: "ts_01",
      name: "testset_d3m0_1",
      feature: "Login",
      author: "tuanld",
      desc: "This is a demo test set.",
      testcases: [
        {
          id: "tc_01",
          name: "testcase_01"
        }
      ]
    },
    {
      id: "ts_02",
      name: "testset_d3m0_2",
      feature: "Homepage",
      author: "tuanld",
      desc: "This is a demo test set.",
      testcases: [
        {
          id: "tc_02",
          name: "testcase_02"
        }
      ]
    },
  ]
}

const reducer = (state = initialState, action) => {
  switch (action.type) {
    case 'CER_LOGIN':
      return {
        ...state,
        isAuthenticated: true,
        activeUser: action.value
      };
    case 'CER_LOGOUT':
      return {
        ...state,
        isAuthenticated: false
      };
    default:
      return state;
  }
}

export default reducer;
