import React from 'react';

const Testsets = props => {
  let selected = 0;
  const setDetail = pos =>{
    selected = pos;
  }
  return(
    <div className="appContent">
      <div className="columns">
        <div className="column is-3">
          <div className="card">
            <header className="card-header">
              <p className="card-header-title">Testsets</p>
            </header>
            <div className="card-table">
              <div className="content">
                <table className="table is-fullwidth is-striped">
                  <tbody>
                    {
                      props.sets.map((testset,i) => {
                        return (
                          <tr className="dataList" key={testset.id} onClick={setDetail(i)}>
                            <td><i class="fas fa-object-group"></i></td>
                            <td>{testset.name}</td>
                            <td className="level-right"><p className="button is-primary is-small">{testset.feature}</p></td>
                          </tr>
                        );
                      })
                    }
                  </tbody>
                </table>
              </div>
            </div>
            <footer className="card-footer"></footer>
          </div>
        </div>
        <div className="column is-9">
          <div className="card">
          <header className="card-header">
              <p className="card-header-title">Detail: {}</p>
            </header>
          </div>
        </div>
      </div>
    </div>
  );
}

export default Testsets;
