const initialState = {
  isAuthenticated: false,
  activeUser: "Master",
  activeProject: "Hello_World"
}

const reducer = (state = initialState, action) => {
  switch (state.type) {
    case 'CER_LOGIN':
      return {
        ...state,
        isAuthenticated: true,
        activeUser: action.value
      };
    case 'CER_LOGOUT':
      return {
        ...state,
        isAuthenticated: false
      };
    default:
      return state;
  }
}

export default reducer;
