import React from 'react';
import { Route, Switch, Redirect} from 'react-router-dom';
import {connect} from 'react-redux';

import LoginCerberus from './auth/login';
import HomeCerberus from './pages/index';

const Cerberus = (props) =>{  
  const rootPath= "/cerberus";

  let data = {
    sidebar_logo: {
      svg: '/logo/Cerberus_logo.svg',
      name: 'cerberus'
    },
    sidebar_items: [
      {
        id:'sb_m_01',
        icon: 'fas fa-file-alt',
        main: 'Documentation',
        subs: [
          {
            id:'sb_s_01_01',
            name: 'Requirement',
            link: '/cerberus/documentation/requirements'
          },
          {
            id:'sb_s_01_02',
            name: 'Test Plan',
            link: '/cerberus/documentation/testplans'
          },
          {
            id:'sb_s_01_03',
            name: 'Report',
            link: '/cerberus/documentation/report'
          }
        ]
      },
      {
        id:'sb_m_02',
        icon: 'fas fa-vial',
        main: 'Tools',
        subs: [
          {
            id:'sb_s_02_01',
            name: 'Overview',
            link: '/cerberus/testmanager/overview'
          },
          {
            id:'sb_s_02_02',
            name: 'Test set',
            link: '/cerberus/testsets'
          },
          {
            id:'sb_s_02_03',
            name: 'Template',
            link: '/cerberus/testmanager/tmp'
          },
          {
            id:'sb_s_02_04',
            name: 'Execution',
            link: '/cerberus/testmanager/execution'
          }
        ]
      },
      {
        id:'sb_m_03',
        icon: 'far fa-smile',
        main: 'User',
        subs: [
          {
            id:'sb_s_03_01',
            name: 'Role',
            link: '/cerberus/user/roles'
          }
        ]
      }
    ]
  };

  const ProtectedRoute = ({children, ...rest}) => {
    return (
      <Route
        {...rest}
        render={() =>
          props.isAuthenticated ? (children) : 
          (<Redirect to={'/cerberus/login'}/>)
        }
      />
    );
  }

  return (
    <div className="Cerberus">
      <Switch>
        <Route path={`${rootPath}/login`}>
          {
            props.isAuthenticated ? <Redirect to={`${rootPath}/`}/> : <LoginCerberus></LoginCerberus>
          }
        </Route>
        <ProtectedRoute path={`${rootPath}/`} exact>
          {<HomeCerberus rootPath={rootPath}></HomeCerberus>} 
        </ProtectedRoute>
      </Switch>
    </div>
  );
}

const mapStateToProps = state => {
  return {
    isAuthenticated: state.isAuthenticated,
    activeUser: state.activeUser,
    activeProject: state.activeProject,
    Testsets: state.Testsets
  };
}

const mapDispatchToProps = dispatch => {
  return {
    changeProject: (name) => dispatch({type: 'CER_PROJ_CHANGE',value: name}),
    logOut: () => dispatch({type: 'CER_LOGOUT'})
  }
}

export default connect(mapStateToProps,mapDispatchToProps)(Cerberus);
