import React from 'react';
import LoginCerberus from './login';
import HomeCerberus from './home';
import { Route, useRouteMatch, Switch, Redirect} from 'react-router-dom';
import { AuthContext, useAuth } from './auth';


const Cerberus = () =>{  
  let match = useRouteMatch();
  const ProtectedRoute = ({children, ...rest}) => {
    const isAuthenticated = useAuth();
    return (
      <Route
        {...rest}
        render={({ location }) =>
          isAuthenticated ? (
            children
          ) : (
            <Redirect
              to={{
                pathname: `${match.url}/login`,
                state: { from: location }
              }}
            />
          )
        }
      />
    );
  }
  const authSuccess = () => {
    // setlogIn(true);
    console.log(isAuthenticated);
  } 
  return (
    <AuthContext.Provider value={false}>
      <div className="Cerberus">
        <Switch>
          <Route path={`${match.path}/login`}>
            {
              isAuthenticated ? <Redirect to={`${match.url}/`}/> : <LoginCerberus success={authSuccess}></LoginCerberus>
            }
          </Route>
          <ProtectedRoute path={`${match.path}/`} exact component={HomeCerberus}/>
          {/* <Route path={`${match.path}/`} exact>
            auth.isAuthenticated ?  <HomeCerberus></HomeCerberus>: <Redirect to={`${match.url}/`}/>
          </Route> */}
        </Switch>
      </div>
    </AuthContext.Provider>
  );
}

export default Cerberus;
